INSERT INTO poems (title, author, dynasty, content, category, tags, analysis, likes, favorites, image) VALUES
('静夜思', '李白', '唐', '床前明月光，疑是地上霜。举头望明月，低头思故乡。', '唐诗', '{"思乡", "月亮", "夜晚"}', '这首诗通过描绘静夜中的月光，表达了诗人对故乡的深切思念之情。语言简洁，意境深远，是李白最著名的代表作之一。', 156, 89, '/images/poem1.jpg'),
('春晓', '孟浩然', '唐', '春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少。', '唐诗', '{"春天", "自然", "生活"}', '描绘春天早晨的生机勃勃景象，表达了对大自然的热爱。语言清新自然，意境优美。', 203, 112, '/images/poem2.jpg'),
('登鹳雀楼', '王之涣', '唐', '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '唐诗', '{"登高", "壮丽", "哲理"}', '通过登高望远的景象，表达了不断进取的人生态度。气势磅礴，富有哲理。', 189, 95, '/images/poem3.jpg'),
('水调歌头·明月几时有', '苏轼', '宋', '明月几时有？把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去，又恐琼楼玉宇，高处不胜寒。起舞弄清影，何似在人间。', '宋词', '{"中秋", "思念", "人生"}', '中秋词中的千古绝唱，表达了对人生的深刻思考。意境开阔，情感真挚。', 278, 167, '/images/poem4.jpg'),
('声声慢·寻寻觅觅', '李清照', '宋', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急？雁过也，正伤心，却是旧时相识。', '宋词', '{"忧伤", "思念", "女性"}', '婉约词的代表作，细腻地表达了深沉的哀愁。语言优美，情感真挚。', 234, 143, '/images/poem5.jpg'),
('青玉案·元夕', '辛弃疾', '宋', '东风夜放花千树，更吹落、星如雨。宝马雕车香满路。凤箫声动，玉壶光转，一夜鱼龙舞。', '宋词', '{"元宵", "热闹", "爱情"}', '描绘元宵佳节的热闹景象，隐含对理想爱情的追求。意境优美，语言华丽。', 198, 121, '/images/poem6.jpg'),
('天净沙·秋思', '马致远', '元', '枯藤老树昏鸦，小桥流水人家，古道西风瘦马。夕阳西下，断肠人在天涯。', '元曲', '{"秋天", "思乡", "旅途"}', '元曲小令的经典之作，通过景物描写表达深沉的思乡之情。意境深远，语言精炼。', 167, 89, '/images/poem7.jpg'),
('虞美人·春花秋月何时了', '李煜', '五代', '春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。雕栏玉砌应犹在，只是朱颜改。问君能有几多愁？恰似一江春水向东流。', '五代词', '{"亡国", "忧愁", "时光"}', '亡国之君的悲歌，表达了对故国的深切怀念和无尽忧愁。情感真挚，意境深远。', 312, 198, '/images/poem8.jpg'),
('望庐山瀑布', '李白', '唐', '日照香炉生紫烟，遥看瀑布挂前川。飞流直下三千尺，疑是银河落九天。', '唐诗', '{"山水", "壮丽", "自然"}', '描绘庐山瀑布的壮丽景象，展现了李白豪放的诗歌风格。想象丰富，气势磅礴。', 245, 134, '/images/poem9.jpg'),
('江雪', '柳宗元', '唐', '千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。', '唐诗', '{"冬天", "孤独", "山水"}', '通过冬日江雪的描写，表达了诗人孤高自许的情怀。意境清冷，语言简练。', 178, 97, '/images/poem10.jpg')
